import React from 'react';
import '../index.css'; // Assuming your CSS file is correctly imported

function Spinner() {
  return (
    <div className='loader' role='status'>
      {/* Optional: Add a descriptive message for accessibility */}
      <span className='sr-only'>Loading...</span>
    </div>
  );
}

export default Spinner;
